import React from 'react';
import { shallow } from 'enzyme';
import { expect } from 'chai';
import Modal from '../src/Modal';

describe('Modal Component', () => {
  it('renders a modal popup if showModal is true', () => {
    const wrapper = shallow(
      <Modal
        modalOverlay="overlay"
        modalContainer="container"
      >
        <div>Pop Up!</div>
      </Modal>
    );
    wrapper.setState({ showModal: true });
    expect(wrapper.find('.om-modal__wrapper').length).to.equal(1);
  });

  it('does not render a modal popup if showModal is false', () => {
    const wrapper = shallow(
      <Modal
        modalOverlay="overlay"
        modalContainer="container"
      >
        <div>Pop Up!</div>
      </Modal>
    );
    wrapper.setState({ showModal: false });
    expect(wrapper.find('.om-modal__wrapper').length).to.equal(0);
  });
});
