import React from 'react';
import { mount } from 'enzyme';
import { expect } from 'chai';
import { stub } from 'sinon';
import Popup from '../src/Popup';

describe('Popup Component', () => {
  it('renders a Popup component', () => {
    const wrapper = mount(<Popup />);
    expect(wrapper.find('.om-popup__container').length).to.equal(1);
  });

  it('calls closeModal when .om-btn--popup-cancel button is clicked', () => {
    const closeModal = stub();
    const wrapper = mount(<Popup closeModal={closeModal} />);

    expect(wrapper.find('.om-btn--popup-cancel').length).to.equal(1);
    wrapper.find('.om-btn--popup-cancel').simulate('click');
    expect(closeModal.callCount).to.equal(1);
  });

  it('calls action when Delete button is clicked', () => {
    const action = stub();
    const wrapper = mount(<Popup action={action} />);

    expect(wrapper.find('.om-btn--popup-delete').length).to.equal(1);
    wrapper.find('.om-btn--popup-delete').simulate('click');
    expect(action.callCount).to.equal(1);
  });
});
